        </svg>
      </div>
      <div class="bubble identite" :style="{ top: `calc(var(--topIdentite) + ${bubblesY['bubbleIdentite'] * 3}%)`, left: `calc(var(--leftIdentite) + ${bubblesX['bubbleIdentite'] * 0.5}%)`}" ref="bubbleIdentite" v-on:click="bubbleClick('bubbleIdentite')"><img src="../assets/id.svg" alt="Identit" ref="bubbleIdentiteImg"></div>
      <div class="bubble cine" :style="{ top: `calc(var(--topCine) + ${bubblesY['bubbleCine'] * 2}%)`, left: `calc(var(--leftCine) + ${bubblesX['bubbleCine'] * 1}%)`}" ref="bubbleCine" v-on:click="bubbleClick('bubbleCine')"><img src="../assets/cine-series.svg" alt="Cinma & sries" ref="bubbleCineImg"></div>
      <div class="bubble streaming" :style="{ bottom: `calc(var(--bottomStreaming) - ${bubblesY['bubbleStreaming'] * 3}%)`, left: `calc(var(--leftStreaming) + ${bubblesX['bubbleStreaming'] * 0.5}%)`}" ref="bubbleStreaming" v-on:click="bubbleClick('bubbleStreaming')"><img src="../assets/streaming.svg" alt="Streaming" ref="bubbleStreamingImg"></div>
      <div class="bubble techno" :style="{ top: `calc(var(--topTechno) + ${bubblesY['bubbleTechno'] * 3}%)`, right: `calc(var(--rightTechno) - ${bubblesX['bubbleTechno'] * 0.5}%)`}" ref="bubbleTechno" v-on:click="bubbleClick('bubbleTechno')"><img src="../assets/techno.svg" alt="Technologies" ref="bubbleTechnoImg"></div>
      <div class="bubble reseaux" :style="{ bottom: `calc(var(--bottomReseaux) - ${bubblesY['bubbleReseaux'] * 2}%)`, right: `calc(var(--rightReseaux) - ${bubblesX['bubbleReseaux'] * 1}%)`}" ref="bubbleReseaux" v-on:click="bubbleClick('bubbleReseaux')"><img src="../assets/reseaux-sociaux.svg" alt="Relations" ref="bubbleReseauxImg"></div>
      <div class="bubble autre" :style="{ bottom: `calc(var(--bottomAutre) - ${bubblesY['bubbleAutre'] * 3}%)`, right: `calc(var(--rightAutre) - ${bubblesX['bubbleAutre'] * 0.5}%)`}" ref="bubbleAutre" v-on:click="bubbleClick('bubbleAutre')"><img src="../assets/autres.svg" alt="Autre" ref="bubbleAutreImg"></div>
    </div>
    <canvas class='main-canvas'></canvas>
  </div>
</template>

<script>
import router from '../router'
import * as THREE from 'three'
import wfrag from '../shaders/wave.frag'
import wvert from '../shaders/wave.vert'
import Easing from '../utils/easing.js'
const { filter, forEach, keys, isEmpty, lowerFirst } = require('lodash')

export default {
  name: 'Specimen',
  data () {
    return {
      mouseX: 0,
      mouseY: 0,
      windowWidth: window.innerWidth,
      windowHeight: window.innerHeight,
      parallax: true,
      animeStart: false,
      finalAnim: false,
      corpsWidth: 0,
      clicked: 'none'
    }
  },
  props: {
    scanned: Boolean
  },
  computed: {
    bonhommeTranslateX: function () {
      return 0 - this.mouseX
    },
    bonhommeTranslateY: function () {
      return 0 - this.mouseY * 20
    },
    bubblesX: function () {
      if (!isEmpty(this.$refs)) {
        const refs = keys(this.$refs)
        const bubbles = filter(refs, key => /^bubble(?!.*Img)/gi.test(key))
        const bubblesX = {}
        forEach(bubbles, key => {
          bubblesX[key] = -(this.mouseX - ((this.$refs[key].getBoundingClientRect().left + (this.$refs[key].offsetWidth / 2)) / this.windowWidth * 2 - 1))
        })
        return bubblesX
      } else {
        return { bubbleIdentite: this.mouseX, bubbleCine: this.mouseX, bubbleStreaming: this.mouseX, bubbleTechno: this.mouseX, bubbleReseaux: this.mouseX, bubbleAutre: this.mouseX }
      }
    },
    bubblesY: function () {
      if (!isEmpty(this.$refs)) {
        const refs = keys(this.$refs)
        const bubbles = filter(refs, key => /^bubble/gi.test(key))
        const bubblesY = {}
        forEach(bubbles, key => {
          bubblesY[key] = -(this.mouseY - ((this.$refs[key].getBoundingClientRect().top + (this.$refs[key].offsetHeight / 2)) / this.windowHeight * 2 - 1))
        })
        return bubblesY
      } else {
        return { bubbleIdentite: this.mouseY, bubbleCine: this.mouseY, bubbleStreaming: this.mouseY, bubbleTechno: this.mouseY, bubbleReseaux: this.mouseY, bubbleAutre: this.mouseY }
      }
    }
  },
  mounted () {
    this.corpsWidth = this.$refs.corps.offsetWidth

    const canvas = document.querySelector('.main-canvas')
    const body = document.querySelector('body')
    body.style.backgroundColor = 'white'
    canvas.style.display = 'none'

    let time = 0.0

    const scene = new THREE.Scene()
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000)
    const renderer = new THREE.WebGLRenderer({
      canvas: canvas,
      alpha: true
    })

    renderer.setSize(window.innerWidth, window.innerHeight)
    renderer.setPixelRatio(window.devicePixelRatio)

    const geometry = new THREE.PlaneBufferGeometry(2, 2, 1, 1)
    const material = new THREE.RawShaderMaterial({
      uniforms: {
        time: { value: 0.0 },
        rez: { type: 'v2', value: [canvas.width, canvas.height] },
        waveColor: { type: 'v3', value: [0.0, 0.0, 0.0] },
        easing: { value: Easing.easeInOutCubic(time * 0.1 + 1.0) }
      },
      vertexShader: wvert,
      fragmentShader: wfrag
    })
    const mesh = new THREE.Mesh(geometry, material)
    scene.add(mesh)

    renderer.render(scene, camera)

    const update = () => {
      requestAnimationFrame(update)

      if (this.animeStart === true) {
        time += 0.1
        material.uniforms.time.value = time
        canvas.style.display = 'block'
        material.uniforms.easing.value = Easing.easeInOutCubic(time * 0.1 + 1.0)
        if (material.uniforms.easing.value > 5.0) {
          this.animeStart = false
          body.style.backgroundColor = 'black'
          if (this.finalAnim === false) {
            router.push(`/data/${this.clicked}`)
          }
        }
      }
      renderer.render(scene, camera)
    }
    requestAnimationFrame(update)

    window.addEventListener('mousemove', (e) => {
      if (this.parallax) {
        this.mouseX = e.clientX / this.windowWidth * 2 - 1
        this.mouseY = e.clientY / this.windowHeight * 2 - 1
      }
    })
    window.addEventListener('resize', () => {
      this.windowWidth = window.innerWidth
      this.windowHeight = window.innerHeight
      renderer.setSize(window.innerWidth, window.innerHeight)
      renderer.setPixelRatio(window.devicePixelRatio)
    })
    if (this.$props.scanned === false) {
      this
        .$anime
        .timeline()
        .add({
          targets: this.$refs.bonhomme,
          left: [0 - this.$refs.bonhomme.offsetWidth, 0],
          duration: 1500,
          easing: 'easeInOutCubic'
        })
        .add({
          targets: this.$refs.scanner,
          height: [1389, 0],
          y: [0, 1389],
          duration: 800,
          delay: 200,
          easing: 'cubicBezier(0.410, 0.615, 0.805, 0.130)'
        })
        .add({
          targets: this.$refs.scanner,
          height: [0, 1389],
          y: [1389, 0],
          duration: 600,
          easing: 'easeInOutCubic'
        })
        .add({
          targets: this.$refs.scanner,
          height: [1389, 0],
          y: [0, 1389],
          duration: 400,
          delay: 400,
          easing: 'cubicBezier(0.975, 0.210, 0.310, 0.720)'
        })
        // .add({
        //   targets: this.$refs.scanner,
        //   height: [0, 1389],
        //   y: [1389, 0],
        //   duration: 400,
        //   delay: 400,
        //   easing: 'easeOutCubic'
        // })
        .add({
          targets: filter(this.$refs, (val, key) => /bubble.*Img/gi.test(key)),
          width: ['0%', '90%'],
          height: ['0%', 'auto'],
          duration: 800,
          delay: this.$anime.stagger(200, { start: 200 }),
          easing: 'easeOutElastic',
          complete: () => {
            this.$emit('isScanned')
          }
        })
    } else {
      body.style.backgroundColor = 'white'
      this.$refs.bonhomme.style.left = 0
      this.$refs.bonhomme.style.opacity = 0
      this.$refs.scanner.style.height = 0
      this.$refs.scanner.style.y = 1389
      filter(this.$refs, (val, key) => /bubble.*Img/gi.test(key)).forEach((val) => {
        val.style.width = '90%'
        val.style.height = 'auto'
      })
      this
        .$anime({
          targets: this.$refs.bonhomme,
          opacity: ['0', '1'],
          duration: 1000,
          delay: 500
        })
    }
  },
  methods: {
    bubbleClick (bubble) {
      this.clicked = lowerFirst(bubble.replace('bubble', '').trim())
      this.parallax = false
      const left = this.$refs[bubble].offsetLeft
      const top = this.$refs[bubble].offsetTop
      this.$refs[bubble].style.left = left + 'px'
      this.$refs[bubble].style.right = ''
      this.$refs[bubble].style.top = top + 'px'
      this.$refs[bubble].style.bottom = ''
      this.$refs[bubble].style.animation = 'none'

      const thisBubble = RegExp('^' + bubble + 'Img$')
      this.$refs[bubble].style.zIndex = 3
      this.$refs[bubble].style.pointerEvents = 'none'
      this
        .$anime
        .timeline()
        .add({
          targets: [filter(this.$refs, (val, key) => /bubble.*Img/g.test(key) && !thisBubble.test(key))],
          translateX: '-100vw',
          duration: 1500,
          easing: 'easeInOutCubic'
        })
        .add({
          targets: [this.$refs.corps, this.$refs.ombre],
          left: 0 - this.windowWidth,
          duration: 1500,
          easing: 'easeInOutCubic',
          complete: () => {
            this.finalAnim = true
            this.animeStart = true
          }
        }, '-= 1500')
        .add({
          targets: this.$refs[bubble],
          translateX: this.windowWidth > 640 ? this.windowWidth - left - 96 - 80 : this.windowWidth - left - (this.windowWidth * 0.05) - 80,
          translateY: this.windowWidth > 640 ? 48 - top : this.windowHeight * 0.04 - top,
          width: 80,
          height: 80,
          minWidth: 0,
          minHeight: 0,
          duration: 1500,
          easing: 'easeInOutCubic',
          complete: () => {
            this.finalAnim = false
            this.$emit('bubbleClick', this.clicked)
            if (this.animeStart === false) {
              router.push(`/data/${this.clicked}`)
            }
          }
        })
        .add({
          targets: this.$refs.bonhomme,
          translateX: 0,
          duration: 500,
          easing: 'easeInOutCubic'
        }, '-=1500')
    }
  }
}
</script>

